import React from 'react'
import { useDispatch } from 'react-redux'
import { registerTech } from '../../../actions/register';
import { useForm } from '../../../hooks/useForm';

const FormaAddTeach = () => {

    const dispatch = useDispatch();
    const [ formValues, handleInputChange ] = useForm({
		name: '',
        descr: '',
        icon: '',
    });

    const { name, descr, icon } = formValues;


//envio a la api
const handleSubmit = (e) => {
    e.preventDefault();
    dispatch( registerTech(formValues) );
    console.log(formValues);
}

    return (
        <div className="login"> 
        <hr/>
            <form onSubmit={ handleSubmit }>
                <div className="col mb-3">
                <label>Name </label>
                <input 
                    className="form-control"
                    type='text'
                    name='name'
                    placeholder='Name'
                    value = { name }
                    onChange={ handleInputChange }
                />
            </div>
            <div className="col mb-3">
                <label> Description </label>
                <textarea
                    className="form-control"
                    rows='10' cols='40'
                    name='descr'
                    placeholder='Desciption'
                    value = { descr }
                    onChange={ handleInputChange }
                />
            </div>
            <div className="col mb-3">
                <label>Seleccione una foto </label>
                    <input 
                        className="form-control"
                        type='file'
                        name='icon'
                        value = { icon }
                        onChange={ handleInputChange }
                    />
            </div>
            <div className="col mb-3">
                
            <button
                className="btn2 btn-primary btn-large btn-block"
                type="submit"					
            >
                Agregar
            </button>

            </div>            
        </form>
    </div>
    )
}

export default FormaAddTeach;
